mapper.select:
select count(1) from t_article a inner join t_category c on a.categoryId = c.id
and c.id = #{categoryId}
where 1 = 1

select DATE_FORMAT(a.createTime,'%Y-%m-%d %H:%i:%s') as createTime, a.*,c.categoryName,c.aliasName from t_article a inner join t_category c on a.categoryId = c.id where 1 = 1
and c.id = #{categoryId}
and a.id < #{articleId}
order by a.id desc limit 10

select DATE_FORMAT(a.createTime,'%Y-%m-%d %H:%i:%s') as createTime, a.*,c.categoryName,c.aliasName from t_article a inner join t_category c on a.categoryId = c.id where 1 = 1 and a.statue = 0
and c.id = #{categoryId}
and a.id > #{articleId}
order by a.id asc limit 1

select DATE_FORMAT(a.createTime,'%Y-%m-%d %H:%i:%s') as createTime, a.*,c.categoryName,c.aliasName from t_article a inner join t_category c on a.categoryId = c.id where 1 = 1 and a.statue = 0
and c.id = #{categoryId}
and a.id < #{articleId}
order by a.id desc limit 1

select DATE_FORMAT(a.createTime,'%Y-%m-%d %H:%i:%s') as createTime, a.*,c.categoryName,c.aliasName from t_article a inner join t_category c on a.categoryId = c.id where 1 = 1 and a.statue = 0
and c.id = #{categoryId}
and a.id != #{articleId}
order by rand() LIMIT 4

select DATE_FORMAT(a.createTime,'%Y-%m-%d %H:%i:%s') as createTime, a.*,c.categoryName,c.aliasName from t_article a inner join t_category c on a.categoryId = c.id where 1 = 1
and exists (select 1 from t_article_tag at where at.articleId = a.id and at.tagId = #{article.tagId})
and a.title like concat('%', #{article.title}, '%')
and a.categoryId = #{article.categoryId}
order by a.id desc limit #{start},#{limit}

mapper.update:
update t_article  
	set categoryId = #{categoryId},
	title = #{title},
	content = #{content},
	description = #{description} 
where id = #{id}
